import {Component} from 'react';
import './App.css';

class App extends Component() {
  state = {
    posts: []
  }

   getPosts = async () => {
    await fetch("/api")
    .then((res) => res.json())
    .then(data => this.setState({posts: data}))
  }

  componentDidMount(){
    this.getPosts()
  }

  render(){
  return (
    <main className="App">
      {this.state.posts.length ? 
  this.state.posts.map(p => (
    <Post post={p} getPosts={this.getPosts} />

      ))
    :
      <h1>Loading...</h1>  
    }
    </main>
  );
}
}

export default App;
